package Soal_Pemro_Comp.soal5;

import java.util.Scanner;

public class soal5_2 {

    /*
    https://projecteuler.net/problem=13
    
     */
    public static void main(String[] args) {
        Scanner x = new Scanner(System.in);
        // long n = x.nextLong();
        // System.out.println("Jumlah digit adalah " + sumDigits(n));

        String digit = x.next();
        System.out.println("Jumlah digit adalah " + sumArr(digit));
        x.close();
    }

    public static int sumDigits(long n){
        String s = String.valueOf(n);
        int num = Integer.parseInt(s), sum = 0, digit, m = (int)n;
        for (int i = 0; i < s.length(); i++){ //1
            sum += num/((int)Math.pow(10, i))%10;
        }

        sum = 0;
        do { //2
            digit = (int)(m%10);
            sum += digit;
            m /= 10;
        } while (m > 0);

        sum = 0;
        m = (int)n;
        while (m > 0) { //3
            digit = (int)(m%10);
            sum += digit;
            m /= 10;
        }
        return sum;
    }

    public static int sumArr(String digit){
        int[] num = new int[digit.length()];
        int sum = 0;
        for (int i = 0; i < digit.length(); i++){
            num[i] = (int)(digit.charAt(i)-48);
            sum += num[i];
        }
        return sum;
    }
}
